import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const CartDetail = () => {
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Hộp Quà Cherry Canada 3kg', price: 2410000, quantity: 1, image: 'link-to-image' },
        { id: 2, name: 'Hộp Quà Cherry Canada 2.8kg', price: 2215000, quantity: 1, image: 'link-to-image' },
    ]);

    // Tính tổng tiền giỏ hàng
    const calculateTotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    // Hàm thay đổi số lượng sản phẩm
    const handleQuantityChange = (id, newQuantity) => {
        setCartItems(cartItems.map(item =>
            item.id === id ? { ...item, quantity: newQuantity } : item
        ));
    };

    // Hàm xóa sản phẩm khỏi giỏ hàng
    const handleRemoveItem = (id) => {
        setCartItems(cartItems.filter(item => item.id !== id));
    };

    return (
        <div className="container mx-auto p-6">
            <h1 className="text-3xl font-bold mb-4">Chi tiết giỏ hàng</h1>
            {cartItems.length === 0 ? (
                <p className="text-gray-600">Giỏ hàng của bạn đang trống.</p>
            ) : (
                <div className="flex flex-col lg:flex-row gap-6">
                    {/* Danh sách sản phẩm trong giỏ hàng */}
                    <div className="flex-1">
                        {cartItems.map(item => (
                            <div key={item.id} className="flex items-center justify-between p-4 border-b">
                                <div className="flex items-center">
                                    <img src={item.image} alt={item.name} className="w-16 h-16 rounded-lg mr-4" />
                                    <div>
                                        <p className="font-semibold">{item.name}</p>
                                        <p className="text-gray-600">{item.price.toLocaleString()}₫</p>
                                    </div>
                                </div>
                                <div className="flex items-center">
                                    <input
                                        type="number"
                                        min="1"
                                        value={item.quantity}
                                        onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                                        className="w-16 text-center border rounded-md p-2"
                                    />
                                    <button
                                        onClick={() => handleRemoveItem(item.id)}
                                        className="ml-4 text-red-500 hover:text-red-700"
                                    >
                                        Xóa
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Thông tin tổng tiền và hành động */}
                    <div className="w-full lg:w-1/3 bg-gray-100 p-6 rounded-lg shadow-lg">
                        <h2 className="text-xl font-bold mb-4">Tóm tắt đơn hàng</h2>
                        <div className="flex justify-between mb-2">
                            <p>Tổng cộng:</p>
                            <p>{calculateTotal().toLocaleString()}₫</p>
                        </div>
                        <Link to="/checkout">
                            <button className="w-full bg-orange-500 text-white py-2 rounded-lg hover:bg-orange-600">
                                Tiến hành thanh toán
                            </button>
                        </Link>
                        <Link to="/product-index">
                            <button className="w-full bg-gray-300 text-black py-2 mt-4 rounded-lg hover:bg-gray-400">
                                Tiếp tục mua hàng
                            </button>
                        </Link>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CartDetail;
