import React from 'react';

const products = [
  {
    id: 1,
    name: 'Xoài Cát Hòa Lộc',
    price: '200,000đ/kg',
    status: 'Còn hàng',
    description: 'Trái cây hữu cơ',
    rating: 4.5,
    image: 'your-image-url.jpg'
  },
  {
    id: 2,
    name: 'Nho Đen Mỹ',
    price: '350,000đ/kg',
    status: 'Còn hàng',
    description: 'Nhập khẩu từ Mỹ',
    rating: 5.0,
    image: 'your-image-url.jpg'
  },
  // Thêm sản phẩm khác ở đây
];

function ProductList({ onSelectProduct }) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      {products.map((product) => (
        <div key={product.id} className="bg-white p-4 shadow-lg rounded-lg">
          <img src={product.image} alt={product.name} className="w-full h-64 object-cover mb-4 rounded-lg" />
          <h3 className="text-2xl font-semibold">{product.name}</h3>
          <p className="text-xl text-green-600">{product.price}</p>
          <p className="text-sm">{product.status}</p>
          <p className="text-sm">{product.description}</p>
          <div className="flex items-center mt-2">
            {[...Array(5)].map((_, i) => (
              <svg
                key={i}
                className={`w-5 h-5 ${i < product.rating ? 'text-yellow-400' : 'text-gray-300'}`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.286 3.957a1 1 0 00.95.691h4.2c.969 0 1.371 1.24.588 1.81l-3.4 2.47a1 1 0 00-.364 1.118l1.286 3.957c.3.921-.755 1.688-1.54 1.118l-3.4-2.47a1 1 0 00-1.176 0l-3.4 2.47c-.784.57-1.838-.197-1.54-1.118l1.286-3.957a1 1 0 00-.364-1.118l-3.4-2.47c-.784-.57-.38-1.81.588-1.81h4.2a1 1 0 00.95-.691l1.286-3.957z" />
              </svg>
            ))}
          </div>
          <button
            className="mt-4 bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-400"
            onClick={() => onSelectProduct(product)}
          >
            Xem chi tiết
          </button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
