import React, { useState } from 'react';

const ImageCarousel = ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    const handlePrev = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
    };

    return (
        <div className="w-full lg:sticky top-0 sm:flex flex-col items-center gap-2">
            <div className="sm:space-y-3 w-full max-sm:flex max-sm:mb-4 max-sm:gap-4">
                <img 
                    src={images[currentIndex]} 
                    alt='' 
                    className="w-full rounded-lg object-cover border-2 border-gray-300 shadow-lg" 
                />
            </div>

            {/* Navigation Controls */}
            <div className="flex justify-center space-x-4 mt-4">
                <button 
                    onClick={handlePrev} 
                    className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-sm font-semibold rounded-lg transition duration-200">
                    Back
                </button>
                <button 
                    onClick={handleNext} 
                    className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-sm font-semibold rounded-lg transition duration-200">
                    Next
                </button>
            </div>

            {/* Thumbnail List */}
            <div className="flex mt-4 space-x-2 overflow-x-auto scrollbar-hide">
                {images.map((image, index) => (
                    <img 
                        key={index} 
                        src={image} 
                        alt={`Thumbnail ${index + 1}`} 
                        className={`w-16 h-16 rounded-md cursor-pointer border-2 transition duration-300 hover:scale-105 ${index === currentIndex ? 'border-green-600 shadow-md' : 'border-gray-300'}`} 
                        onClick={() => setCurrentIndex(index)} 
                    />
                ))}
            </div>
        </div>
    );
};

export default ImageCarousel;
