import React, { useState } from 'react';

const CheckoutView = () => {
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Cây cảnh nhỏ', price: 150000, quantity: 3, image: 'link-to-image' },
        { id: 2, name: 'Cây cảnh lớn', price: 250000, quantity: 6, image: 'link-to-image' }
    ]);

    const [shippingInfo, setShippingInfo] = useState({
        name: '',
        address: '',
        city: '',
        phone: '',
        note: '',
        shippingMethod: 'standard'
    });

    const [paymentMethod, setPaymentMethod] = useState('COD');
    const [agreeTerms, setAgreeTerms] = useState(false);

    const [discountCode, setDiscountCode] = useState('');
    const [membership, setMembership] = useState(false);
    const [discountApplied, setDiscountApplied] = useState(false);
    const [discountAmount, setDiscountAmount] = useState(0);

    const shippingFee = shippingInfo.shippingMethod === 'express' ? 50000 : 30000;

    // Tính tổng giá trị đơn hàng (trước giảm giá)
    const calculateSubtotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    // Tính tổng giá trị đơn hàng (sau giảm giá)
    const calculateTotal = () => {
        const subtotal = calculateSubtotal();
        const total = subtotal - discountAmount + shippingFee;
        return total > 0 ? total : 0; // Tổng không bao giờ âm
    };

    // Áp dụng mã giảm giá
    const handleApplyDiscount = () => {
        if (discountCode === 'GIAM10') {
            setDiscountAmount(calculateSubtotal() * 0.1); // 10% giảm giá
            setDiscountApplied(true);
        } else if (membership) {
            setDiscountAmount(calculateSubtotal() * 0.05); // 5% cho thành viên
            setDiscountApplied(true);
        } else {
            alert('Mã giảm giá không hợp lệ!');
        }
    };

    // Thay đổi số lượng sản phẩm
    const handleQuantityChange = (id, delta) => {
        setCartItems(cartItems.map(item =>
            item.id === id ? { ...item, quantity: item.quantity + delta > 0 ? item.quantity + delta : 1 } : item
        ));
    };

    // Xóa sản phẩm khỏi giỏ hàng
    const handleRemoveItem = (id) => {
        setCartItems(cartItems.filter(item => item.id !== id));
    };

    return (
        <div className="container mx-auto p-6">
            <h1 className="text-3xl font-bold mb-4">Thanh toán</h1>
            <div className="flex flex-col lg:flex-row gap-6">
                {/* Thông tin giỏ hàng */}
                <div className="flex-1">
                    <h2 className="text-xl font-bold mb-4">Thông tin giỏ hàng</h2>
                    <table className="min-w-full bg-white">
                        <thead>
                            <tr className="text-left">
                                <th className="px-4 py-2">Sản phẩm</th>
                                <th className="px-4 py-2">Giá</th>
                                <th className="px-4 py-2">Số lượng</th>
                                <th className="px-4 py-2">Tổng</th>
                                <th className="px-4 py-2">Sửa</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cartItems.map(item => (
                                <tr key={item.id} className="border-b">
                                    <td className="px-4 py-2 flex items-center">
                                        <img src={item.image} alt={item.name} className="w-16 h-16 rounded-lg mr-4" />
                                        <span>{item.name}</span>
                                    </td>
                                    <td className="px-4 py-2">{item.price.toLocaleString()}₫</td>
                                    <td className="px-4 py-2 flex items-center">
                                        <button onClick={() => handleQuantityChange(item.id, -1)} className="px-2 border">-</button>
                                        <input type="text" value={item.quantity} readOnly className="w-12 text-center border mx-2" />
                                        <button onClick={() => handleQuantityChange(item.id, 1)} className="px-2 border">+</button>
                                    </td>
                                    <td className="px-4 py-2">{(item.price * item.quantity).toLocaleString()}₫</td>
                                    <td className="px-4 py-2">
                                        <button onClick={() => handleRemoveItem(item.id)} className="text-red-500">Xóa</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="mt-4">
                        <p className="text-lg font-semibold">Tổng cộng: {calculateSubtotal().toLocaleString()}₫</p>
                    </div>
                </div>

                {/* Thông tin giao hàng */}
                <div className="w-full lg:w-1/3 bg-gray-100 p-6 rounded-lg shadow-lg">
                    <h2 className="text-xl font-bold mb-4">Thông tin giao hàng</h2>
                    <div className="mb-4">
                        <label className="block mb-2">Họ và tên</label>
                        <input type="text" className="w-full p-2 border rounded-lg" value={shippingInfo.name} onChange={(e) => setShippingInfo({ ...shippingInfo, name: e.target.value })} />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Địa chỉ</label>
                        <input type="text" className="w-full p-2 border rounded-lg" value={shippingInfo.address} onChange={(e) => setShippingInfo({ ...shippingInfo, address: e.target.value })} />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Thành phố</label>
                        <input type="text" className="w-full p-2 border rounded-lg" value={shippingInfo.city} onChange={(e) => setShippingInfo({ ...shippingInfo, city: e.target.value })} />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Số điện thoại</label>
                        <input type="text" className="w-full p-2 border rounded-lg" value={shippingInfo.phone} onChange={(e) => setShippingInfo({ ...shippingInfo, phone: e.target.value })} />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Phương thức giao hàng</label>
                        <select className="w-full p-2 border rounded-lg" value={shippingInfo.shippingMethod} onChange={(e) => setShippingInfo({ ...shippingInfo, shippingMethod: e.target.value })}>
                            <option value="standard">Tiêu chuẩn (30,000₫)</option>
                            <option value="express">Nhanh (50,000₫)</option>
                        </select>
                    </div>
                </div>
            </div>

            {/* Phần mã giảm giá và thành viên */}
            <div className="bg-gray-100 p-6 rounded-lg shadow-lg mt-6">
                <h2 className="text-xl font-bold mb-4">Mã giảm giá và thành viên</h2>
                <div className="mb-4">
                    <input
                        type="text"
                        className="w-full p-2 border rounded-lg"
                        placeholder="Nhập mã giảm giá"
                        value={discountCode}
                        onChange={(e) => setDiscountCode(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <label className="flex items-center">
                        <input
                            type="checkbox"
                            className="mr-2"
                            checked={membership}
                            onChange={() => setMembership(!membership)}
                        />
                        Thành viên? Áp dụng ưu đãi đặc biệt
                    </label>
                </div>
                <button className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600" onClick={handleApplyDiscount}>
                    Áp dụng
                </button>
            </div>

            {/* Thông tin thanh toán */}
            <div className="bg-gray-100 p-6 rounded-lg shadow-lg mt-6">
                <h2 className="text-xl font-bold mb-4">Thông tin thanh toán</h2>
                <div className="flex justify-between mb-2">
                    <p>Tổng giá sản phẩm:</p>
                    <p>{calculateSubtotal().toLocaleString()}₫</p>
                </div>
                {discountApplied && (
                    <div className="flex justify-between mb-2">
                        <p>Giảm giá:</p>
                        <p>-{discountAmount.toLocaleString()}₫</p>
                    </div>
                )}
                <div className="flex justify-between mb-2">
                    <p>Phí vận chuyển:</p>
                    <p>{shippingFee.toLocaleString()}₫</p>
                </div>
                <div className="flex justify-between mb-2">
                    <p>Tổng tiền thanh toán:</p>
                    <p>{calculateTotal().toLocaleString()}₫</p>
                </div>
            </div>
        </div>
    );
};

export default CheckoutView;
