import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const CartDetail = () => {
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Táo nhập khẩu', price: 70000, quantity: 1, image: 'link-to-image' }
    ]);

    // Tính tổng tiền giỏ hàng
    const calculateTotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    // Hàm thay đổi số lượng sản phẩm
    const handleQuantityChange = (id, delta) => {
        setCartItems(cartItems.map(item =>
            item.id === id ? { ...item, quantity: item.quantity + delta > 0 ? item.quantity + delta : 1 } : item
        ));
    };

    // Hàm xóa sản phẩm khỏi giỏ hàng
    const handleRemoveItem = (id) => {
        setCartItems(cartItems.filter(item => item.id !== id));
    };

    return (
        <div className="container mx-auto p-6">
            <h1 className="text-2xl font-bold mb-4">Chi tiết giỏ hàng</h1>
            {cartItems.length === 0 ? (
                <p className="text-gray-600">Giỏ hàng của bạn đang trống.</p>
            ) : (
                <div className="flex flex-col lg:flex-row gap-6">
                    {/* Danh sách sản phẩm */}
                    <div className="flex-1">
                        <table className="min-w-full bg-white">
                            <thead>
                                <tr className="text-left">
                                    <th className="px-4 py-2">Sản phẩm</th>
                                    <th className="px-4 py-2">Giá</th>
                                    <th className="px-4 py-2">Số lượng</th>
                                    <th className="px-4 py-2">Tổng</th>
                                </tr>
                            </thead>
                            <tbody>
                                {cartItems.map(item => (
                                    <tr key={item.id} className="border-b">
                                        <td className="px-4 py-2 flex items-center">
                                            <button onClick={() => handleRemoveItem(item.id)} className="text-red-500 mr-2">
                                                X
                                            </button>
                                            <img src={item.image} alt={item.name} className="w-16 h-16 rounded-lg mr-4" />
                                            <span>{item.name}</span>
                                        </td>
                                        <td className="px-4 py-2">{item.price.toLocaleString()}₫</td>
                                        <td className="px-4 py-2 flex items-center">
                                            <button
                                                onClick={() => handleQuantityChange(item.id, -1)}
                                                className="px-2 border"
                                            >-</button>
                                            <input
                                                type="text"
                                                value={item.quantity}
                                                readOnly
                                                className="w-12 text-center border mx-2"
                                            />
                                            <button
                                                onClick={() => handleQuantityChange(item.id, 1)}
                                                className="px-2 border"
                                            >+</button>
                                        </td>
                                        <td className="px-4 py-2">{(item.price * item.quantity).toLocaleString()}₫</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        {/* Nút tiếp tục mua hàng và cập nhật giỏ hàng */}
                        <div className="flex justify-between mt-4">
                            <Link to="/product-index" className="text-green-500 border border-green-500 px-4 py-2 rounded-lg hover:bg-green-100">
                                ← Tiếp tục xem sản phẩm
                            </Link>
                            <button className="text-white bg-green-500 px-4 py-2 rounded-lg hover:bg-green-600">
                                Cập nhật giỏ hàng
                            </button>
                        </div>
                    </div>

                    {/* Phần tổng tiền */}
                    <div className="w-full lg:w-1/3 bg-gray-100 p-6 rounded-lg shadow-lg">
                        <h2 className="text-xl font-bold mb-4">Tổng số lượng</h2>
                        <div className="flex justify-between mb-2">
                            <p>Tổng phụ:</p>
                            <p>{calculateTotal().toLocaleString()}₫</p>
                        </div>
                        <div className="flex justify-between mb-2">
                            <p>Tổng:</p>
                            <p>{calculateTotal().toLocaleString()}₫</p>
                        </div>
                        <Link to="/checkout">
                            <button className="w-full bg-black text-white py-2 rounded-lg hover:bg-gray-800">
                                Tiến hành thanh toán
                            </button>
                        </Link>
                        <div className="mt-4">
                            <button className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600">
                                Phiếu ưu đãi
                            </button>
                            <div className="mt-2">
                                <input
                                    type="text"
                                    placeholder="Mã ưu đãi"
                                    className="w-full p-2 border rounded-lg"
                                />
                                <button className="w-full bg-gray-300 text-black py-2 mt-2 rounded-lg hover:bg-gray-400">
                                    Áp dụng
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CartDetail;
